package compiler488.codegen;

import compiler488.runtime.Machine;

import java.util.ArrayList;

/**
 * Created by isthisnagee on 4/3/17.
 */
public class MachineUtils {
    /**
     * Get the number of lines generated by this sequence of instructions
     * @param instruction
     */
    public int numLines(ArrayList<Instruction> instruction) {
       return instruction.size();
    }

    /**
     * Return a check for `a > b`
     * @param a
     * @param b
     * @return
     */
    public ArrayList<Instruction> MachineGT(ArrayList<Instruction> a, ArrayList<Instruction> b) {
        ArrayList<Instruction> result = new ArrayList<>();
        result.addAll(b);
        result.addAll(a);
        result.add(new Instruction(Machine.LT));
        return new ArrayList<>();
    }

    /**
     * Return instructinos for `a >= b`
     * @param a
     * @param b
     * @return
     */
    public ArrayList<Instruction> MachineGTE(ArrayList<Instruction> a, ArrayList<Instruction> b) {
        return new ArrayList<>();
    }

    /**
     * Return instructinos for `a <= b`
     * @param a
     * @param b
     * @return
     */
    public ArrayList<Instruction> MachineLTE(ArrayList<Instruction> a, ArrayList<Instruction> b) {
        return new ArrayList<>();
    }

}
